# editor_capture/__init__.py
# UE5 Editor Capture Module
#
# A comprehensive toolkit for capturing screenshots in UE5 Editor.
# Includes orbital multi-angle capture, PIE capture, asset editor operations, and window capture.
#
# Usage:
#   import editor_capture
#
#   # Orbital screenshots (SceneCapture2D)
#   editor_capture.take_orbital_screenshots(world, target_location=unreal.Vector(0,0,100))
#
#   # PIE screenshots (auto-capture during Play In Editor)
#   editor_capture.start_pie_capture(auto_start_pie=True, multi_angle=True)
#   editor_capture.stop_pie_capture()
#
#   # Asset editor operations
#   editor_capture.open_asset_editor("/Game/Blueprints/BP_MyActor")
#   editor_capture.close_asset_editor("/Game/Blueprints/BP_MyActor")
#
#   # Window capture (Windows only)
#   editor_capture.capture_ue5_window("C:/Screenshots/editor.png")
#
# Or import submodules directly:
#   from editor_capture import orbital, pie_capture, asset_editor, window_capture

